import React, { useState } from 'react';
import { useEffect } from 'react';
// import bgimage from "./components/rose-petals.svg"

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // simulate loading data from an API
    setTimeout(() => {
      setProjects([
        { name: 'Project 1', description: 'This is a description of Project 1' },
        { name: 'Project 2', description: 'This is a description of Project 2' },
        { name: 'Project 3', description: 'This is a description of Project 3' },
      ]);
      setLoading(false);
    }, 2000);
  }, []);

  return (
    <div id='particles'>


    <div id='intro'>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="#">My Portfolio</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a className="nav-link" href="#my-projects">My Projects</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#my-tech-stack">My Tech Stack</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#my-interest">My Interest</a>
            </li>
          </ul>
        </div>
      </nav>
      {loading ? (
        <div className="d-flex justify-content-center">
          <div className="spinner-border" role="status">
            <span className="sr-only">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="container">
          <h1 className="text-center m-5" id="my-projects">My Projects</h1>
          <div className="row">
            {projects.map((project, index) => (
              <div key={index} className="col-md-4">
                                <div className="card">
                  <div className="card-body">
                    <h2 className="card-title">{project.name}</h2>
                    <p className="card-text">{project.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="row" id="my-tech-stack">
            <div className="col-md-12">
              <h2>My Tech Stack</h2>
              <p>List your technologies and skills here</p>
            </div>
          </div>
          <div className="row" id="my-interest">
            <div className="col-md-12">
              <h2>My Interest</h2>
              <p>List your interests here</p>
            </div>
          </div>
        </div>
      )}
      </div> 

    </div>
  );
}

export default App;
