import React, { useState } from 'react';
import { useEffect } from 'react';
// import bgimage from "./components/rose-petals.svg"

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // simulate loading data from an API
    setTimeout(() => {
      setProjects([
        { name: 'Project 1', description: 'This is a description of Project 1' },
        { name: 'Project 2', description: 'This is a description of Project 2' },
        { name: 'Project 3', description: 'This is a description of Project 3' },
      ]);
      setLoading(false);
    }, 2000);
  }, []);

  return (
    <div className="App">


      <div className='intro'>

        <header id="header" style={{backgroundColor:"rgba(0,0,0,0.7)"}}>
          <div class="d-flex flex-column">

            <div class="profile">
              <img src="assets/profile-img.png" alt="" class="img-fluid rounded-circle" />
              <h1 class="text-light"><a href="index.html">Shivam Verma</a></h1>
              <div class="social-links mt-3 text-center">
                <a href="https://www.facebook.com/x.shiVam.07" class="facebook"><i class="bx bxl-facebook"></i></a>
                <a href="https://www.instagram.com/shivam.0303/" class="instagram"><i class="bx bxl-instagram"></i></a>
                <a href="https://join.skype.com/invite/q7wqE8LcLSk2" class="google-plus"><i class="bx bxl-skype"></i></a>
                <a href="https://www.linkedin.com/in/shivam0303/" class="linkedin"><i class="bx bxl-linkedin"></i></a>
              </div>
            </div>

            <nav id="navbar" class="nav-menu navbar">
              <ul>
                <li><a href="#hero" class="nav-link scrollto active"><i class="bx bx-home"></i> <span>Home</span></a></li>
                <li><a href="#about" class="nav-link scrollto"><i class="bx bx-user"></i> <span>About</span></a></li>
                <li><a href="#resume" class="nav-link scrollto"><i class="bx bx-file-blank"></i> <span>Resume</span></a></li>
                <li><a href="#portfolio" class="nav-link scrollto"><i class="bx bx-book-content"></i> <span>Portfolio</span></a></li>
                <li><a href="#contact" class="nav-link scrollto"><i class="bx bx-envelope"></i> <span>Contact</span></a></li>
              </ul>
            </nav>
          </div>
        </header>

        {loading ? (
          <div className="d-flex justify-content-center">
            <div className="spinner-border" role="status">
              <span className="sr-only">Loading...</span>
            </div>
          </div>
        ) : (
          <div>
            <h2>Shivam Verma</h2>
          </div>
        )}
      </div>

    </div>
  );
}

export default App;
