import React, { useState } from 'react';
import { useEffect } from 'react';
// import bgimage from "./components/rose-petals.svg"
import Typewriter from "typewriter-effect";
import Home from './components/home';


function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // simulate loading data from an API
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, []);

  return (
    <div className="App">


      <div className='intro'>
        <i className="bi bi-list mobile-nav-toggle d-xl-none"></i>
        <header id="header" style={{ backgroundColor: "rgba(0,0,0,0.7)" }}>
          <div className="d-flex flex-column">

            <div className="profile">
              <img src="assets/profile-img.png" alt="" className="img-fluid rounded-circle" />
              <h1 className="text-light"><a href="index.html">Shivam Verma</a></h1>
              <div className="social-links mt-3 text-center">
                <a href="https://www.facebook.com/x.shiVam.07" className="facebook"><i className="bx bxl-facebook"></i></a>
                <a href="https://www.instagram.com/shivam.0303/" className="instagram"><i className="bx bxl-instagram"></i></a>
                <a href="https://join.skype.com/invite/q7wqE8LcLSk2" className="google-plus"><i className="bx bxl-skype"></i></a>
                <a href="https://www.linkedin.com/in/shivam0303/" className="linkedin"><i className="bx bxl-linkedin"></i></a>
              </div>
            </div>

            <nav id="navbar" className="nav-menu navbar">
              <ul>
                <li><a href="#hero" className="nav-link scrollto active"><i className="bx bx-home"></i> <span>Home</span></a></li>
                <li><a href="#about" className="nav-link scrollto"><i className="bx bx-user"></i> <span>About</span></a></li>
                <li><a href="#resume" className="nav-link scrollto"><i className="bx bx-file-blank"></i> <span>Resume</span></a></li>
                <li><a href="#portfolio" className="nav-link scrollto"><i className="bx bx-book-content"></i> <span>Projects</span></a></li>
                <li><a href="#contact" className="nav-link scrollto"><i className="bx bx-envelope"></i> <span>Contact</span></a></li>
              </ul>
            </nav>
          </div>
        </header>

        {loading ? (
          <div className="d-flex justify-content-center">
            <div className="spinner-border" role="status">
              <span className="sr-only">Loading...</span>
            </div>
          </div>
        ) : (
          <div>
            <section id="hero" className="d-flex flex-column justify-content-center align-items-center">
              <div className="hero-container" >
                <Home/>
              </div>
            </section>
          </div>
        )}
      </div>

    </div>
  );
}

export default App;
